INSERT INTO faqs (language, question, answer_short, answer_long, slug, category, tags, keywords, voice_queries, target_areas, property_types, meta_title, meta_description, is_featured, sort_order) VALUES
('fr', 'Quel est le prix moyen de l''immobilier à Costa del Sol?', 'Les prix varient selon l''emplacement, de 150 000€ à plus de 2M€ pour les propriétés de luxe.', 'Les prix de l''immobilier à Costa del Sol varient considérablement selon l''emplacement et le type de propriété. Les zones intérieures commencent à environ 150 000€ pour les appartements, tandis que les propriétés en bord de mer dans des emplacements privilégiés comme Marbella peuvent dépasser 2 millions d''euros. Le prix moyen d''un appartement de 2 chambres varie de 200 000€ à 400 000€, tandis que les villas commencent généralement à partir de 500 000€ et peuvent atteindre plusieurs millions pour les propriétés de luxe avec vue sur mer.', 'prix-moyen-immobilier-costa-del-sol', 'buying', ARRAY['prix', 'marché', 'investissement'], ARRAY['prix immobilier', 'prix Costa del Sol', 'marché immobilier', 'valeurs propriétés'], ARRAY['combien coûtent les propriétés à Costa del Sol', 'prix des maisons Costa del Sol'], ARRAY['Marbella', 'Estepona', 'Fuengirola'], ARRAY['appartement', 'villa'], 'Prix Immobilier Moyen Costa del Sol 2024', 'Découvrez les prix immobiliers actuels à Costa del Sol. Des appartements abordables aux villas de luxe.', true, 15),

('de', 'Wie hoch ist der durchschnittliche Immobilienpreis an der Costa del Sol?', 'Immobilienpreise variieren je nach Lage, von €150.000 bis €2M+ für Luxusimmobilien.', 'Die Immobilienpreise an der Costa del Sol variieren erheblich je nach Lage und Immobilientyp. Inland-Gebiete beginnen bei etwa €150.000 für Apartments, während Strandimmobilien in erstklassigen Lagen wie Marbella über €2 Millionen kosten können. Der Durchschnittspreis für eine 2-Zimmer-Wohnung liegt zwischen €200.000-€400.000, während Villen typischerweise ab €500.000 beginnen und mehrere Millionen für Luxusimmobilien mit Meerblick erreichen können.', 'durchschnittliche-immobilienpreise-costa-del-sol', 'buying', ARRAY['Preise', 'Markt', 'Investition'], ARRAY['Immobilienpreise', 'Costa del Sol Preise', 'Immobilienmarkt', 'Immobilienwerte'], ARRAY['was kosten Immobilien an der Costa del Sol', 'Hauspreise Costa del Sol'], ARRAY['Marbella', 'Estepona', 'Fuengirola'], ARRAY['Apartment', 'Villa'], 'Durchschnittliche Immobilienpreise Costa del Sol 2024', 'Entdecken Sie aktuelle Immobilienpreise an der Costa del Sol. Von erschwinglichen Apartments bis Luxusvillen.', true, 15),

('pl', 'Jaka jest średnia cena nieruchomości w Costa del Sol?', 'Ceny nieruchomości różnią się w zależności od lokalizacji, od €150,000 do €2M+ za luksusowe nieruchomości.', 'Ceny nieruchomości w Costa del Sol znacznie różnią się w zależności od lokalizacji i typu nieruchomości. Obszary śródlądowe zaczynają się od około €150,000 za apartamenty, podczas gdy nieruchomości przy plaży w najlepszych lokalizacjach jak Marbella mogą przekraczać €2 miliony. Średnia cena za 2-pokojowy apartament waha się od €200,000-€400,000, podczas gdy wille zazwyczaj zaczynają się od €500,000 i mogą osiągnąć kilka milionów za luksusowe nieruchomości z widokiem na morze.', 'srednia-cena-nieruchomosci-costa-del-sol', 'buying', ARRAY['ceny', 'rynek', 'inwestycja'], ARRAY['ceny nieruchomości', 'ceny Costa del Sol', 'rynek nieruchomości', 'wartości nieruchomości'], ARRAY['ile kosztują nieruchomości w Costa del Sol', 'ceny domów Costa del Sol'], ARRAY['Marbella', 'Estepona', 'Fuengirola'], ARRAY['apartament', 'willa'], 'Średnie Ceny Nieruchomości Costa del Sol 2024', 'Poznaj aktualne ceny nieruchomości w Costa del Sol. Od przystępnych apartamentów po luksusowe wille.', true, 15),

('sv', 'Vad är det genomsnittliga fastighetspriset i Costa del Sol?', 'Fastighetspriser varierar beroende på läge, från €150,000 till €2M+ för lyxfastigheter.', 'Fastighetspriser i Costa del Sol varierar avsevärt beroende på läge och fastighetstyp. Inland-områden börjar från cirka €150,000 för lägenheter, medan strandfastigheter i förstklassiga lägen som Marbella kan överstiga €2 miljoner. Genomsnittspriset för en 2-rumslägenhet varierar från €200,000-€400,000, medan villor vanligtvis börjar från €500,000 och kan nå flera miljoner för lyxfastigheter med havsutsikt.', 'genomsnittliga-fastighetspriser-costa-del-sol', 'buying', ARRAY['priser', 'marknad', 'investering'], ARRAY['fastighetspriser', 'Costa del Sol priser', 'fastighetsmarknad', 'fastighetsvärden'], ARRAY['vad kostar fastigheter i Costa del Sol', 'huspriser Costa del Sol'], ARRAY['Marbella', 'Estepona', 'Fuengirola'], ARRAY['lägenhet', 'villa'], 'Genomsnittliga Fastighetspriser Costa del Sol 2024', 'Upptäck aktuella fastighetspriser i Costa del Sol. Från prisvärda lägenheter till lyxvillor.', true, 15),

('da', 'Hvad er den gennemsnitlige ejendomspris i Costa del Sol?', 'Ejendomspriser varierer efter beliggenhed, fra €150.000 til €2M+ for luksusejendomme.', 'Ejendomspriser i Costa del Sol varierer betydeligt efter beliggenhed og ejendomstype. Indlandsområder starter fra omkring €150.000 for lejligheder, mens strandejendomme i førsteklasses beliggenheder som Marbella kan overstige €2 millioner. Gennemsnitsprisen for en 2-værelses lejlighed varierer fra €200.000-€400.000, mens villaer typisk starter fra €500.000 og kan nå flere millioner for luksusejendomme med havudsigt.', 'gennemsnitlige-ejendomspriser-costa-del-sol', 'buying', ARRAY['priser', 'marked', 'investering'], ARRAY['ejendomspriser', 'Costa del Sol priser', 'ejendomsmarked', 'ejendomsværdier'], ARRAY['hvad koster ejendomme i Costa del Sol', 'huspriser Costa del Sol'], ARRAY['Marbella', 'Estepona', 'Fuengirola'], ARRAY['lejlighed', 'villa'], 'Gennemsnitlige Ejendomspriser Costa del Sol 2024', 'Opdag aktuelle ejendomspriser i Costa del Sol. Fra overkommelige lejligheder til luksusvillaer.', true, 15);