-- Insert Dutch translations for all English FAQs
INSERT INTO faqs (language, question, answer_short, answer_long, slug, category, tags, location, meta_title, meta_description, keywords, voice_queries, target_areas, property_types, is_featured, sort_order)
SELECT 
  'nl' as language,
  CASE 
    WHEN question = 'What are the costs involved in buying property in Costa del Sol?' THEN 'Wat zijn de kosten bij het kopen van vastgoed in Costa del Sol?'
    WHEN question = 'Do I need a Spanish bank account to buy property?' THEN 'Heb ik een Spaanse bankrekening nodig om vastgoed te kopen?'
    WHEN question = 'What is the average property price in Marbella?' THEN 'Wat is de gemiddelde vastgoedprijs in Marbella?'
    WHEN question = 'How long does the property buying process take?' THEN 'Hoe lang duurt het vastgoedkoopproces?'
    WHEN question = 'What taxes do I need to pay when buying property?' THEN 'Welke belastingen moet ik betalen bij het kopen van vastgoed?'
    WHEN question = 'Can foreigners buy property in Spain?' THEN 'Kunnen buitenlanders vastgoed kopen in Spanje?'
    WHEN question = 'What is NIE number and how do I get it?' THEN 'Wat is een NIE-nummer en hoe krijg ik het?'
    WHEN question = 'Do I need a lawyer when buying property in Spain?' THEN 'Heb ik een advocaat nodig bij het kopen van vastgoed in Spanje?'
    WHEN question = 'What is the difference between new and resale properties?' THEN 'Wat is het verschil tussen nieuwe en bestaande woningen?'
    WHEN question = 'How much deposit do I need for a Spanish mortgage?' THEN 'Hoeveel aanbetaling heb ik nodig voor een Spaanse hypotheek?'
    WHEN question = 'What are the ongoing costs of owning property in Spain?' THEN 'Wat zijn de doorlopende kosten van het bezitten van vastgoed in Spanje?'
    WHEN question = 'Can I get a mortgage as a non-resident?' THEN 'Kan ik een hypotheek krijgen als niet-ingezetene?'
    WHEN question = 'What is IBI tax and how much is it?' THEN 'Wat is IBI-belasting en hoeveel is het?'
    WHEN question = 'Do I need property insurance in Spain?' THEN 'Heb ik een opstalverzekering nodig in Spanje?'
    WHEN question = 'What is the best time to buy property in Costa del Sol?' THEN 'Wat is de beste tijd om vastgoed te kopen in Costa del Sol?'
    WHEN question = 'How do I find a reliable real estate agent?' THEN 'Hoe vind ik een betrouwbare makelaar?'
    WHEN question = 'What should I look for in a property inspection?' THEN 'Waar moet ik op letten bij een woninginspectie?'
    WHEN question = 'Can I rent out my property for vacation rentals?' THEN 'Kan ik mijn woning verhuren voor vakantieverhuringen?'
    WHEN question = 'What are the capital gains taxes in Spain?' THEN 'Wat zijn de vermogenswinstbelastingen in Spanje?'
    WHEN question = 'How do I transfer utilities to my name?' THEN 'Hoe zet ik nutsvoorzieningen op mijn naam?'
    WHEN question = 'What is the average rental yield in Costa del Sol?' THEN 'Wat is het gemiddelde huurrendement in Costa del Sol?'
    WHEN question = 'Do I need to declare my Spanish property in my home country?' THEN 'Moet ik mijn Spaanse vastgoed aangeven in mijn thuisland?'
    WHEN question = 'What is the process for buying off-plan property?' THEN 'Wat is het proces voor het kopen van nieuwbouwwoningen?'
    WHEN question = 'How do I get residency in Spain through property investment?' THEN 'Hoe krijg ik een verblijfsvergunning in Spanje door vastgoedinvestering?'
    WHEN question = 'What are the best areas to invest in Costa del Sol?' THEN 'Wat zijn de beste gebieden om te investeren in Costa del Sol?'
    WHEN question = 'Can I buy property through a Spanish company?' THEN 'Kan ik vastgoed kopen via een Spaans bedrijf?'
    WHEN question = 'What is the Golden Visa program?' THEN 'Wat is het Golden Visa-programma?'
    WHEN question = 'How do I calculate the total cost of buying property?' THEN 'Hoe bereken ik de totale kosten van het kopen van vastgoed?'
    WHEN question = 'What is the role of a notary in Spanish property purchases?' THEN 'Wat is de rol van een notaris bij Spaanse vastgoedaankopen?'
    WHEN question = 'Can I get a survey done on the property?' THEN 'Kan ik een taxatie laten doen van het vastgoed?'
    WHEN question = 'What happens if the seller has debts on the property?' THEN 'Wat gebeurt er als de verkoper schulden heeft op het vastgoed?'
    WHEN question = 'How do I check if a property has legal issues?' THEN 'Hoe controleer ik of een woning juridische problemen heeft?'
    WHEN question = 'What is the difference between escritura and registro?' THEN 'Wat is het verschil tussen escritura en registro?'
    WHEN question = 'Can I negotiate the price of a property?' THEN 'Kan ik onderhandelen over de prijs van een woning?'
    WHEN question = 'What is included in the purchase price?' THEN 'Wat is inbegrepen in de aankoopprijs?'
    WHEN question = 'How do I set up direct debits for Spanish bills?' THEN 'Hoe stel ik automatische incasso''s in voor Spaanse rekeningen?'
    WHEN question = 'What is the community fee and what does it cover?' THEN 'Wat is de gemeenschapskosten en wat dekt het?'
    WHEN question = 'Can I buy property with cryptocurrency?' THEN 'Kan ik vastgoed kopen met cryptocurrency?'
    WHEN question = 'What is the inheritance tax in Spain?' THEN 'Wat is de erfbelasting in Spanje?'
    WHEN question = 'How do I find out about new developments?' THEN 'Hoe kom ik te weten over nieuwe ontwikkelingen?'
    WHEN question = 'What are the benefits of buying new construction?' THEN 'Wat zijn de voordelen van het kopen van nieuwbouw?'
    WHEN question = 'Can I view properties virtually?' THEN 'Kan ik woningen virtueel bekijken?'
    WHEN question = 'What is the process for making an offer?' THEN 'Wat is het proces voor het doen van een bod?'
    WHEN question = 'How do I get financing for property investment?' THEN 'Hoe krijg ik financiering voor vastgoedinvestering?'
    WHEN question = 'What are the rental laws in Spain?' THEN 'Wat zijn de huurwetten in Spanje?'
    WHEN question = 'Can I buy property in a Spanish trust?' THEN 'Kan ik vastgoed kopen in een Spaanse trust?'
    WHEN question = 'What is the process for buying a commercial property?' THEN 'Wat is het proces voor het kopen van commercieel vastgoed?'
    WHEN question = 'How do I handle property management from abroad?' THEN 'Hoe beheer ik vastgoed vanuit het buitenland?'
    WHEN question = 'What are the swimming pool regulations?' THEN 'Wat zijn de zwembadregels?'
    WHEN question = 'Can I build additional structures on my property?' THEN 'Kan ik extra structuren bouwen op mijn vastgoed?'
    WHEN question = 'What is the process for property renovation?' THEN 'Wat is het proces voor woningrenovatie?'
    WHEN question = 'How do I get building permits?' THEN 'Hoe krijg ik bouwvergunningen?'
    WHEN question = 'What are the energy efficiency requirements?' THEN 'Wat zijn de energie-efficiëntie vereisten?'
    WHEN question = 'Can I install solar panels on my property?' THEN 'Kan ik zonnepanelen installeren op mijn vastgoed?'
    WHEN question = 'What is the process for selling property in Spain?' THEN 'Wat is het proces voor het verkopen van vastgoed in Spanje?'
    WHEN question = 'How do I determine the market value of my property?' THEN 'Hoe bepaal ik de marktwaarde van mijn vastgoed?'
    WHEN question = 'What are the costs of selling property?' THEN 'Wat zijn de kosten van het verkopen van vastgoed?'
    WHEN question = 'Can I exchange my property for another?' THEN 'Kan ik mijn vastgoed ruilen voor een ander?'
    WHEN question = 'What happens to my property when I die?' THEN 'Wat gebeurt er met mijn vastgoed als ik sterf?'
    WHEN question = 'How do I transfer property ownership?' THEN 'Hoe draag ik eigendom van vastgoed over?'
    WHEN question = 'What is usufruct and how does it work?' THEN 'Wat is vruchtgebruik en hoe werkt het?'
    WHEN question = 'Can I leave my property to non-Spanish residents?' THEN 'Kan ik mijn vastgoed nalaten aan niet-Spaanse inwoners?'
    WHEN question = 'What are the property management options?' THEN 'Wat zijn de vastgoedbeheer opties?'
    WHEN question = 'How do I handle emergency repairs?' THEN 'Hoe ga ik om met noodreparaties?'
    WHEN question = 'What insurance do I need for rental properties?' THEN 'Welke verzekering heb ik nodig voor huurwoningen?'
    ELSE question
  END as question,
  CASE 
    WHEN answer_short = 'Buying costs typically include 10-15% of purchase price for taxes, legal fees, and notary costs.' THEN 'Aankoopkosten bedragen doorgaans 10-15% van de aankoopprijs voor belastingen, juridische kosten en notariskosten.'
    WHEN answer_short = 'While not mandatory, having a Spanish bank account simplifies the buying process and ongoing payments.' THEN 'Hoewel niet verplicht, vereenvoudigt een Spaanse bankrekening het aankoopproces en lopende betalingen.'
    WHEN answer_short = 'Average property prices in Marbella range from €400,000 to €1.5 million depending on location and type.' THEN 'Gemiddelde vastgoedprijzen in Marbella variëren van €400.000 tot €1,5 miljoen afhankelijk van locatie en type.'
    WHEN answer_short = 'The property buying process typically takes 6-12 weeks from offer acceptance to completion.' THEN 'Het vastgoedkoopproces duurt doorgaans 6-12 weken van bod acceptatie tot voltooiing.'
    WHEN answer_short = 'You''ll pay transfer tax (6-10%), VAT (10% for new properties), and various fees totaling 10-15%.' THEN 'U betaalt overdrachtsbelasting (6-10%), BTW (10% voor nieuwe woningen), en verschillende kosten totaal 10-15%.'
    WHEN answer_short = 'Yes, foreigners can freely buy property in Spain with the same rights as Spanish citizens.' THEN 'Ja, buitenlanders kunnen vrijelijk vastgoed kopen in Spanje met dezelfde rechten als Spaanse burgers.'
    WHEN answer_short = 'NIE (Número de Identificación de Extranjero) is a tax identification number required for all property transactions.' THEN 'NIE (Número de Identificación de Extranjero) is een belastingidentificatienummer vereist voor alle vastgoedtransacties.'
    WHEN answer_short = 'While not legally required, hiring a lawyer is highly recommended to protect your interests.' THEN 'Hoewel niet wettelijk verplicht, wordt het inhuren van een advocaat sterk aanbevolen om uw belangen te beschermen.'
    WHEN answer_short = 'New properties come with warranties and modern features, while resales may offer better locations and character.' THEN 'Nieuwe woningen komen met garanties en moderne functies, terwijl bestaande woningen betere locaties en karakter kunnen bieden.'
    WHEN answer_short = 'Non-residents typically need 20-30% deposit, while residents may get mortgages with 10-20% down.' THEN 'Niet-inwoners hebben doorgaans 20-30% aanbetaling nodig, terwijl inwoners hypotheken kunnen krijgen met 10-20% eigen inbreng.'
    WHEN answer_short = 'Annual costs include IBI tax, community fees, insurance, utilities, and maintenance (typically 2-4% of property value).' THEN 'Jaarlijkse kosten omvatten IBI-belasting, gemeenschapskosten, verzekering, nutsvoorzieningen en onderhoud (doorgaans 2-4% van vastgoedwaarde).'
    WHEN answer_short = 'Yes, non-residents can get mortgages but with higher deposit requirements and interest rates.' THEN 'Ja, niet-inwoners kunnen hypotheken krijgen maar met hogere aanbetaling vereisten en rentetarieven.'
    WHEN answer_short = 'IBI (Impuesto sobre Bienes Inmuebles) is annual property tax ranging from 0.4-1.1% of cadastral value.' THEN 'IBI (Impuesto sobre Bienes Inmuebles) is jaarlijkse onroerende zaakbelasting van 0,4-1,1% van kadastrale waarde.'
    WHEN answer_short = 'Property insurance is not legally required but highly recommended and often required by mortgage lenders.' THEN 'Opstalverzekering is niet wettelijk verplicht maar sterk aanbevolen en vaak vereist door hypotheekverstrekkers.'
    WHEN answer_short = 'Spring and autumn offer the best weather for viewings, while winter may provide better negotiating opportunities.' THEN 'Lente en herfst bieden het beste weer voor bezichtigingen, terwijl winter betere onderhandelingsmogelijkheden kan bieden.'
    WHEN answer_short = 'Look for agents with proper credentials, local market knowledge, and good client testimonials.' THEN 'Zoek naar makelaars met juiste referenties, lokale marktkennis en goede klantreferenties.'
    WHEN answer_short = 'Check structural integrity, plumbing, electrical systems, legal documentation, and potential issues.' THEN 'Controleer structurele integriteit, leidingwerk, elektrische systemen, juridische documentatie en potentiële problemen.'
    WHEN answer_short = 'Yes, but check local regulations and obtain proper licenses for short-term vacation rentals.' THEN 'Ja, maar controleer lokale regelgeving en verkrijg juiste vergunningen voor kortetermijn vakantieverhuringen.'
    WHEN answer_short = 'Capital gains tax ranges from 19-23% for residents and 24% for non-residents on property profits.' THEN 'Vermogenswinstbelasting varieert van 19-23% voor inwoners en 24% voor niet-inwoners op vastgoedwinsten.'
    WHEN answer_short = 'Contact utility companies directly or through your lawyer to transfer electricity, water, gas, and internet services.' THEN 'Neem direct contact op met nutsbedrijven of via uw advocaat om elektriciteit, water, gas en internetdiensten over te zetten.'
    WHEN answer_short = 'Rental yields in Costa del Sol typically range from 4-8% annually depending on location and property type.' THEN 'Huurrendementen in Costa del Sol variëren doorgaans van 4-8% jaarlijks afhankelijk van locatie en vastgoedtype.'
    WHEN answer_short = 'Yes, most countries require you to declare foreign property ownership for tax purposes.' THEN 'Ja, de meeste landen vereisen dat u buitenlands vastgoedbezit aangeeft voor belastingdoeleinden.'
    WHEN answer_short = 'Off-plan purchases require staged payments, bank guarantees, and careful contract review for completion dates.' THEN 'Nieuwbouwaankopen vereisen gefaseerde betalingen, bankgaranties en zorgvuldige contractbeoordeling voor opleverdatums.'
    WHEN answer_short = 'Invest €500,000+ in Spanish property to qualify for residency through the Golden Visa program.' THEN 'Investeer €500.000+ in Spaans vastgoed om in aanmerking te komen voor verblijfsvergunning via het Golden Visa-programma.'
    WHEN answer_short = 'Top investment areas include Marbella, Estepona, Benalmádena, Puerto Banús, and emerging coastal towns.' THEN 'Top investeringsgebieden zijn Marbella, Estepona, Benalmádena, Puerto Banús en opkomende kustplaatsen.'
    WHEN answer_short = 'Yes, buying through a Spanish company can offer tax advantages but requires careful legal structuring.' THEN 'Ja, kopen via een Spaans bedrijf kan belastingvoordelen bieden maar vereist zorgvuldige juridische structurering.'
    WHEN answer_short = 'Golden Visa grants residency to non-EU investors purchasing property worth €500,000 or more.' THEN 'Golden Visa verleent verblijfsvergunning aan non-EU investeerders die vastgoed kopen ter waarde van €500.000 of meer.'
    WHEN answer_short = 'Add purchase price + taxes (10-15%) + legal fees + ongoing costs for accurate total investment calculation.' THEN 'Tel aankoopprijs + belastingen (10-15%) + juridische kosten + lopende kosten op voor nauwkeurige totale investeringsberekening.'
    WHEN answer_short = 'Notaries verify legal documents, conduct final checks, and officially register the property transfer.' THEN 'Notarissen verifiëren juridische documenten, voeren eindcontroles uit en registreren officieel de vastgoedoverdracht.'
    WHEN answer_short = 'Yes, independent surveys help identify structural issues, legal problems, and accurate property valuations.' THEN 'Ja, onafhankelijke taxaties helpen structurele problemen, juridische problemen en nauwkeurige vastgoedwaarderingen identificeren.'
    WHEN answer_short = 'Debts typically transfer with the property, so thorough legal checks are essential before purchase.' THEN 'Schulden gaan doorgaans over met het vastgoed, dus grondige juridische controles zijn essentieel voor aankoop.'
    WHEN answer_short = 'Check the Land Registry, obtain legal certificates, and hire a lawyer for comprehensive due diligence.' THEN 'Controleer het kadaster, verkrijg juridische certificaten en huur een advocaat in voor uitgebreide due diligence.'
    WHEN answer_short = 'Escritura is the title deed, while registro is the official registration at the Land Registry office.' THEN 'Escritura is de eigendomsakte, terwijl registro de officiële registratie bij het kadaster is.'
    WHEN answer_short = 'Yes, property prices are often negotiable, especially in slower markets or for properties needing work.' THEN 'Ja, vastgoedprijzen zijn vaak onderhandelbaar, vooral in tragere markten of voor woningen die werk nodig hebben.'
    WHEN answer_short = 'Purchase price typically includes the property and fixed installations, but verify what''s included in contracts.' THEN 'Aankoopprijs omvat doorgaans het vastgoed en vaste installaties, maar verifieer wat er in contracten is inbegrepen.'
    WHEN answer_short = 'Visit your bank with property documents and Spanish tax number to set up automatic payments.' THEN 'Bezoek uw bank met vastgoeddocumenten en Spaans belastingnummer om automatische betalingen in te stellen.'
    WHEN answer_short = 'Community fees cover shared area maintenance, security, gardens, pools, and building management services.' THEN 'Gemeenschapskosten dekken onderhoud van gemeenschappelijke ruimtes, beveiliging, tuinen, zwembaden en gebouwbeheerdiensten.'
    WHEN answer_short = 'Some developers accept cryptocurrency, but traditional financing and legal frameworks are still preferred.' THEN 'Sommige ontwikkelaars accepteren cryptocurrency, maar traditionele financiering en juridische kaders hebben nog steeds de voorkeur.'
    WHEN answer_short = 'Inheritance tax varies by region (7.65-34%) and relationship to deceased, with some exemptions available.' THEN 'Erfbelasting varieert per regio (7,65-34%) en relatie tot overledene, met enkele vrijstellingen beschikbaar.'
    WHEN answer_short = 'Follow real estate websites, contact developers directly, and work with agents specializing in new developments.' THEN 'Volg vastgoedwebsites, neem direct contact op met ontwikkelaars en werk met makelaars gespecialiseerd in nieuwe ontwikkelingen.'
    WHEN answer_short = 'New construction offers warranties, modern features, energy efficiency, and customization options.' THEN 'Nieuwbouw biedt garanties, moderne functies, energie-efficiëntie en aanpassingsopties.'
    WHEN answer_short = 'Yes, virtual tours, video calls, and detailed photography allow remote property viewing and selection.' THEN 'Ja, virtuele tours, videogesprekken en gedetailleerde fotografie maken op afstand vastgoedbezoek en selectie mogelijk.'
    WHEN answer_short = 'Submit written offers through your agent, including price, conditions, and proposed completion timeline.' THEN 'Dien schriftelijke biedingen in via uw makelaar, inclusief prijs, voorwaarden en voorgestelde voltooiingstijdlijn.'
    WHEN answer_short = 'Explore Spanish mortgages, international lenders, private financing, or investment partnerships for funding.' THEN 'Verken Spaanse hypotheken, internationale kredietverstrekkers, private financiering of investeringspartnerschappen voor financiering.'
    WHEN answer_short = 'Spanish rental laws protect tenants with minimum lease terms and regulated rent increase procedures.' THEN 'Spaanse huurwetten beschermen huurders met minimale huurtermijnen en gereguleerde huurverhogingsprocedures.'
    WHEN answer_short = 'Trust structures are complex in Spain; consult specialized lawyers for proper legal and tax setup.' THEN 'Truststructuren zijn complex in Spanje; raadpleeg gespecialiseerde advocaten voor juiste juridische en belastingopstelling.'
    WHEN answer_short = 'Commercial property purchases follow similar processes but with additional business licensing and zoning checks.' THEN 'Commerciële vastgoedaankopen volgen vergelijkbare processen maar met aanvullende bedrijfsvergunningen en bestemmingscontroles.'
    WHEN answer_short = 'Hire local property managers, use technology for remote monitoring, and maintain regular communication.' THEN 'Huur lokale vastgoedbeheerders, gebruik technologie voor monitoring op afstand en onderhoud regelmatige communicatie.'
    WHEN answer_short = 'Pool regulations cover safety barriers, permits, water quality standards, and maintenance requirements.' THEN 'Zwembadregels dekken veiligheidsbarrières, vergunningen, waterkwaliteitsnormen en onderhoudsvereisten.'
    WHEN answer_short = 'Check local building regulations and obtain proper permits before adding structures or extensions.' THEN 'Controleer lokale bouwregels en verkrijg juiste vergunningen voordat u structuren of uitbreidingen toevoegt.'
    WHEN answer_short = 'Renovation requires permits, licensed contractors, and compliance with local building codes and heritage rules.' THEN 'Renovatie vereist vergunningen, erkende aannemers en naleving van lokale bouwcodes en erfgoedregels.'
    WHEN answer_short = 'Apply for building permits through local town halls with architectural plans and technical specifications.' THEN 'Vraag bouwvergunningen aan via lokale gemeentehuizen met architecturale plannen en technische specificaties.'
    WHEN answer_short = 'Properties must meet energy efficiency standards with certificates showing energy performance ratings.' THEN 'Woningen moeten voldoen aan energie-efficiëntienormen met certificaten die energieprestatiebeoordelingen tonen.'
    WHEN answer_short = 'Yes, solar panel installation is encouraged with permits and potential government incentives available.' THEN 'Ja, installatie van zonnepanelen wordt aangemoedigd met vergunningen en mogelijke overheidssubsidies beschikbaar.'
    WHEN answer_short = 'Selling involves valuation, marketing, legal documentation, and completion similar to purchasing process.' THEN 'Verkopen omvat waardering, marketing, juridische documentatie en voltooiing vergelijkbaar met aankoopproces.'
    WHEN answer_short = 'Use professional appraisers, compare similar sales, and consider current market conditions for accurate valuation.' THEN 'Gebruik professionele taxateurs, vergelijk vergelijkbare verkopen en overweeg huidige marktomstandigheden voor nauwkeurige waardering.'
    WHEN answer_short = 'Selling costs include agent fees (3-5%), legal fees, capital gains tax, and administrative expenses.' THEN 'Verkoopkosten omvatten makelaarskosten (3-5%), juridische kosten, vermogenswinstbelasting en administratieve uitgaven.'
    WHEN answer_short = 'Property exchanges are possible but require careful valuation matching and complex legal documentation.' THEN 'Vastgoedruilen zijn mogelijk maar vereisen zorgvuldige waardering matching en complexe juridische documentatie.'
    WHEN answer_short = 'Property passes through inheritance laws with potential tax implications depending on beneficiaries and value.' THEN 'Vastgoed gaat over via erfwetten met mogelijke belastinggevolgen afhankelijk van begunstigden en waarde.'
    WHEN answer_short = 'Property ownership transfer requires legal documentation, notarization, and official registration processes.' THEN 'Vastgoedeigendomsoverdracht vereist juridische documentatie, notariële bekrachtiging en officiële registratieprocessen.'
    WHEN answer_short = 'Usufruct grants usage rights without ownership, commonly used in inheritance and financial planning.' THEN 'Vruchtgebruik verleent gebruiksrechten zonder eigendom, vaak gebruikt bij erfenis en financiële planning.'
    WHEN answer_short = 'Yes, but inheritance taxes and legal procedures may apply depending on beneficiary''s tax residence status.' THEN 'Ja, maar erfbelastingen en juridische procedures kunnen van toepassing zijn afhankelijk van de belastingverblijfstatus van de begunstigde.'
    WHEN answer_short = 'Choose between self-management, local agents, or specialized property management companies for maintenance and rentals.' THEN 'Kies tussen zelfbeheer, lokale makelaars of gespecialiseerde vastgoedbeheermaatschappijen voor onderhoud en verhuur.'
    WHEN answer_short = 'Establish relationships with local contractors and maintain emergency fund for unexpected repair situations.' THEN 'Vestig relaties met lokale aannemers en onderhoud noodfonds voor onverwachte reparatiesituaties.'
    WHEN answer_short = 'Rental properties require comprehensive insurance covering property damage, liability, and potential rental income loss.' THEN 'Huurwoningen vereisen uitgebreide verzekering die vastgoedschade, aansprakelijkheid en mogelijk huurinkomstenverlies dekt.'
    ELSE answer_short
  END as answer_short,
  answer_long,
