    WHEN question = 'What is the average rental yield in Costa del Sol?' THEN 'Quel est le rendement locatif moyen en Costa del Sol ?'
    WHEN question = 'Do I need to declare my Spanish property in my home country?' THEN 'Dois-je déclarer mon bien espagnol dans mon pays d''origine ?'
    WHEN question = 'What is the process for buying off-plan property?' THEN 'Quel est le processus d''achat d''un bien sur plan ?'
    WHEN question = 'How do I get residency in Spain through property investment?' THEN 'Comment obtenir la résidence en Espagne par l''investissement immobilier ?'
    WHEN question = 'What are the best areas to invest in Costa del Sol?' THEN 'Quelles sont les meilleures zones d''investissement en Costa del Sol ?'
    WHEN question = 'Can I buy property through a Spanish company?' THEN 'Puis-je acheter un bien par l''intermédiaire d''une société espagnole ?'
    WHEN question = 'What is the Golden Visa program?' THEN 'Qu''est-ce que le programme Golden Visa ?'
    WHEN question = 'How do I calculate the total cost of buying property?' THEN 'Comment calculer le coût total d''achat d''un bien immobilier ?'
    WHEN question = 'What is the role of a notary in Spanish property purchases?' THEN 'Quel est le rôle d''un notaire dans les achats immobiliers espagnols ?'
    WHEN question = 'Can I get a survey done on the property?' THEN 'Puis-je faire effectuer une expertise du bien ?'
    WHEN question = 'What happens if the seller has debts on the property?' THEN 'Que se passe-t-il si le vendeur a des dettes sur le bien ?'
    WHEN question = 'How do I check if a property has legal issues?' THEN 'Comment vérifier si un bien a des problèmes juridiques ?'
    WHEN question = 'What is the difference between escritura and registro?' THEN 'Quelle est la différence entre escritura et registro ?'
    WHEN question = 'Can I negotiate the price of a property?' THEN 'Puis-je négocier le prix d''un bien ?'
    WHEN question = 'What is included in the purchase price?' THEN 'Qu''est-ce qui est inclus dans le prix d''achat ?'
    WHEN question = 'How do I set up direct debits for Spanish bills?' THEN 'Comment mettre en place des prélèvements automatiques pour les factures espagnoles ?'
    WHEN question = 'What is the community fee and what does it cover?' THEN 'Qu''est-ce que les charges de copropriété et que couvrent-elles ?'
    WHEN question = 'Can I buy property with cryptocurrency?' THEN 'Puis-je acheter un bien avec de la cryptomonnaie ?'
    WHEN question = 'What is the inheritance tax in Spain?' THEN 'Qu''est-ce que les droits de succession en Espagne ?'
    WHEN question = 'How do I find out about new developments?' THEN 'Comment me renseigner sur les nouveaux développements ?'
    WHEN question = 'What are the benefits of buying new construction?' THEN 'Quels sont les avantages d''acheter du neuf ?'
    WHEN question = 'Can I view properties virtually?' THEN 'Puis-je visiter des biens virtuellement ?'
    WHEN question = 'What is the process for making an offer?' THEN 'Quel est le processus pour faire une offre ?'
    WHEN question = 'How do I get financing for property investment?' THEN 'Comment obtenir un financement pour l''investissement immobilier ?'
    WHEN question = 'What are the rental laws in Spain?' THEN 'Quelles sont les lois sur la location en Espagne ?'
    WHEN question = 'Can I buy property in a Spanish trust?' THEN 'Puis-je acheter un bien dans un trust espagnol ?'
    WHEN question = 'What is the process for buying a commercial property?' THEN 'Quel est le processus d''achat d''un bien commercial ?'
    WHEN question = 'How do I handle property management from abroad?' THEN 'Comment gérer un bien depuis l''étranger ?'
    WHEN question = 'What are the swimming pool regulations?' THEN 'Quelles sont les réglementations sur les piscines ?'
    WHEN question = 'Can I build additional structures on my property?' THEN 'Puis-je construire des structures supplémentaires sur mon bien ?'
    WHEN question = 'What is the process for property renovation?' THEN 'Quel est le processus de rénovation d''un bien ?'
    WHEN question = 'How do I get building permits?' THEN 'Comment obtenir des permis de construire ?'
    WHEN question = 'What are the energy efficiency requirements?' THEN 'Quelles sont les exigences d''efficacité énergétique ?'
    WHEN question = 'Can I install solar panels on my property?' THEN 'Puis-je installer des panneaux solaires sur mon bien ?'
    WHEN question = 'What is the process for selling property in Spain?' THEN 'Quel est le processus de vente d''un bien en Espagne ?'
    WHEN question = 'How do I determine the market value of my property?' THEN 'Comment déterminer la valeur marchande de mon bien ?'
    WHEN question = 'What are the costs of selling property?' THEN 'Quels sont les coûts de vente d''un bien ?'
    WHEN question = 'Can I exchange my property for another?' THEN 'Puis-je échanger mon bien contre un autre ?'
    WHEN question = 'What happens to my property when I die?' THEN 'Que devient mon bien quand je meurs ?'
